import React, { useCallback } from 'react';

import { makeStyles } from '@mui/styles';
import {FormControl, FormControlLabel, FormLabel, Radio, RadioGroup} from "@mui/material";

const useStyles = makeStyles({
  formControl: {
    marginBottom: '20px',
  },
});

const gameMode = ({ mode, setMode }) => {
  const classes = useStyles();
  const handleModeChange = useCallback((e) => setMode(e.target.value), [setMode]);

  return (
    <FormControl component="fieldset" className={classes.formControl}>
      <FormLabel component="legend">Modo de Juego</FormLabel>
      <RadioGroup row value={mode} onChange={handleModeChange}>
        <FormControlLabel value="player" control={<Radio />} label="Jugador vs Jugador" />
        <FormControlLabel value="pc" control={<Radio />} label="Jugador vs PC" />
      </RadioGroup>
    </FormControl>
  );
};

export default gameMode();
