import React from 'react';
import {makeStyles} from '@mui/styles';
import useTicTacToe from '../ticTacToe/useTicTacToe';
import GameSelector from './GameSelector';
import GameMode from './GameMode';
import {Button, Grid, Typography} from '@mui/material';

const useStyles = makeStyles({
  container: {
    textAlign: 'center',
    padding: '20px',
  },
  gridItem: {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    border: '1px solid black',
    height: '100px', // Asegura que sea cuadrado
    width: '100px', // Asegura que sea cuadrado
    borderRadius: '10px',
    margin: '5px', // Añade separación entre cuadros
  },
  button: {
    width: '100%',
    height: '100%',
    borderRadius: '10px', // Bordes redondeados para el botón también
  },
});

const TicTacToe = () => {
  const classes = useStyles();
  const {
    board,
    playerSymbol,
    mode,
    winner,
    setPlayerSymbol,
    setMode,
    handleClick,
    resetGame,
  } = useTicTacToe();

  return (
    <div className={classes.container}>
      <GameSelector
        playerSymbol={playerSymbol}
        setPlayerSymbol={setPlayerSymbol}
      />
      <GameMode mode={mode} setMode={setMode} />
      <Grid container justifyContent="center">
        {board.map((cell, index) => (
          <Grid
            item
            key={index}
            xs={4}
            sm={4}
            md={4}
            lg={4}
            className={classes.gridItem}
          >
            <Button
              variant="outlined"
              onClick={() => handleClick(index)}
              className={classes.button}
            >
              {cell}
            </Button>
          </Grid>
        ))}
      </Grid>
      {winner && (
        <Typography variant="h5" gutterBottom>
          {winner === 'Empate'
            ? 'La partida queda empatada'
            : `Ganan las ${winner}`}
        </Typography>
      )}
      <Button variant="contained" color="primary" onClick={resetGame}>
        Reiniciar Juego
      </Button>
    </div>
  );
};

export default TicTacToe;
