import React, { useState, useEffect } from 'react';
import Slider from 'react-slick';
import { Card, CardContent, CardMedia, Typography } from '@mui/material';
import { fetchNews } from '../api/axios';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';

const News = () => {
  const [news, setNews] = useState([]);

  useEffect(() => {
    const getNews = async () => {
      const newsData = await fetchNews();
      setNews(newsData);
    };

    getNews();
  }, []);

  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
  };

  return (
    <>
      <Typography variant="h5" gutterBottom>Noticias de Desarrollo Tecnológico</Typography>
      <Slider {...settings}>
        {news.map((article, index) => (
          <Card key={index} style={{ maxWidth: 600, margin: 'auto' }}>
            {article.urlToImage && (
              <CardMedia
                component="img"
                height="140"
                image={article.urlToImage}
                alt={article.title}
              />
            )}
            <CardContent>
              <Typography variant="h6" gutterBottom>{article.title}</Typography>
              <Typography variant="body2" color="textSecondary" paragraph>
                {article.description}
              </Typography>
              <Typography variant="body2" color="textSecondary" align="right">
                <a href={article.url} target="_blank" rel="noopener noreferrer">Leer más</a>
              </Typography>
            </CardContent>
          </Card>
        ))}
      </Slider>
    </>
  );
};

export default News;
