import React from 'react';
import { List, ListItem, ListItemText, Button, Checkbox } from '@mui/material';
import { makeStyles } from '@mui/styles';

const useStyles = makeStyles({
  listItem: {
    display: 'flex',
    alignItems: 'center',
  },
});

const TaskList = ({ filteredTasks, toggleTaskCompletion, editTask, deleteTask }) => {
  const classes = useStyles();

  return (
    <List>
      {filteredTasks.map((task, index) => (
        <ListItem key={index} className={classes.listItem}>
          <Checkbox
            checked={task.completed}
            onChange={() => toggleTaskCompletion(index)}
          />
          <ListItemText primary={task.text} />
          <Button onClick={() => editTask(index)} variant="outlined" color="primary" edge="end">
            Edit
          </Button>
          <Button onClick={() => deleteTask(index)} variant="outlined" color="secondary" edge="end">
            Delete
          </Button>
        </ListItem>
      ))}
    </List>
  );
};

export default TaskList;
