import React, { useState } from 'react';
import { fetchWeather } from '../api/axios';
import { Button, CardContent, TextField, Typography } from "@mui/material";
import Card from "@mui/material/Card";

const Weather = () => {
  const [city, setCity] = useState('');
  const [weather, setWeather] = useState(null);

  const handleFetchWeather = async () => {
    try {
      const validatetime = '2024-09-15T00:00:00Z'; // Ejemplo de fecha y hora válida
      const parameters = 't_2m:C'; // Ejemplo de parámetro
      const locations = city; // Usar la ciudad ingresada como ubicación
      const format = 'json'; // Formato de respuesta
      const optionals = {}; // Parámetros opcionales si los hay

      const weatherData = await fetchWeather(validatetime, parameters, locations, format, optionals);
      setWeather(weatherData);
    } catch (error) {
      console.error('Error fetching weather:', error);
    }
  };

  return (
    <>
      <Typography variant="h5" gutterBottom>Clima</Typography>
      <TextField
        label="Ciudad"
        value={city}
        onChange={(e) => setCity(e.target.value)}
        style={{ marginBottom: 20 }}
      />
      <Button variant="contained" color="primary" onClick={handleFetchWeather}>
        Obtener Clima
      </Button>
      {weather && (
        <Card style={{ maxWidth: 600, margin: '20px auto' }}>
          <CardContent>
            <Typography variant="h6">{weather.name}</Typography>
            <Typography variant="body1">Temperatura: {weather.main.temp}°C</Typography>
            <Typography variant="body1">Condición: {weather.weather[0].description}</Typography>
          </CardContent>
        </Card>
      )}
    </>
  );
};

export default Weather;
