import axios from 'axios';


const username = 'desarrolloweb_socarras_eddy';
const password = 'zP0IbXj3u4';


const newsApiKey = 'YOUR_NEWS_API_KEY';


export const fetchNews = async () => {
  try {
    const response = await axios.get(`https://newsapi.org/v2/everything?q=technology&apiKey=${newsApiKey}`);
    return response.data.articles;
  } catch (error) {
    console.error('Error fetching news:', error);
    throw error;
  }
};


export const fetchWeather = async (city) => {
  try {
    const response = await axios.get(`https://api.openweathermap.org/data/2.5/weather?q=${city}&units=metric`, {
      auth: {
        username: username,
        password: password
      }
    });
    return response.data;
  } catch (error) {
    console.error('Error fetching weather:', error);
    throw error;
  }
};
