import axios from 'axios';

const username = 'desarrolloweb_socarras_eddy';
const password = 'zP0IbXj3u4';

const fetchWeather = async (validdatetime, parameters, locations, format, optionals) => {
  try {
    const response = await axios.get(`https://api.meteomatics.com/${validdatetime}/${parameters}/${locations}/${format}`, {
      headers: {
        'Authorization': `Basic ${btoa(`${username}:${password}`)}`
      },
      params: optionals
    });
    return response.data;
  } catch (error) {
    console.error('Error fetching weather:', error);
    throw error;
  }
};

export default fetchWeather;
