import React, { useState } from 'react';
import fetchWeather from '../api/axios';
import { Button, CardContent, TextField, Typography, Dialog, DialogTitle, DialogContent, DialogContentText, DialogActions } from '@mui/material';

const Weather = () => {
  const [city, setCity] = useState('');
  const [weather, setWeather] = useState(null);
  const [open, setOpen] = useState(false);

  const handleFetchWeather = async () => {
    try {
      const validatetime = '2024-09-15T00:00:00Z';
      const parameters = 't_2m:C';
      const locations = city;
      const format = 'json';
      const optionals = {};

      const weatherData = await fetchWeather(
        validatetime,
        parameters,
        locations,
        format,
        optionals
      );
      setWeather(weatherData);
      setOpen(true);
    } catch (error) {
      console.error('Error fetching weather:', error);
    }
  };

  const handleClose = () => {
    setOpen(false);
  };

  return (
    <>
      <Typography variant="h5" gutterBottom>
        Clima
      </Typography>
      <TextField
        label="Ciudad"
        value={city}
        onChange={(e) => setCity(e.target.value)}
        style={{ marginBottom: 20 }}
      />
      <Button variant="contained" color="primary" onClick={handleFetchWeather}>
        Obtener Clima
      </Button>
      <Dialog
        open={open}
        onClose={handleClose}
        aria-labelledby="weather-dialog-title"
        aria-describedby="weather-dialog-description"
      >
        <DialogTitle id="weather-dialog-title">Clima en {weather?.name}</DialogTitle>
        <DialogContent>
          <DialogContentText id="weather-dialog-description">
            <Typography variant="body1">
              Temperatura: {weather?.main?.temp}°C
            </Typography>
            <Typography variant="body1">
              Condición: {weather?.weather ? weather.weather[0].description : ''}
            </Typography>
          </DialogContentText>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose} color="primary">Cerrar</Button>
        </DialogActions>
      </Dialog>
    </>
  );
};

export default Weather;
